package consumer;import database.databaseAccess;import jakarta.servlet.ServletException;import jakarta.servlet.http.HttpServlet;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import java.io.IOException;import java.sql.SQLException;public class booking extends HttpServlet {    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        String[] input = req.getParameter("appointmentId").split(",");        String userId = input[1];        String sql = "delete from appointment where appointmentId=";        String appointmentId = input[0];        sql += appointmentId;        databaseAccess a = new databaseAccess();        a.execute(sql);        req.setAttribute("userId",userId);        req.getRequestDispatcher("consumer.jsp").forward(req,resp);    }    @Override    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        databaseAccess a = new databaseAccess();        String userId = req.getParameter("userId");        String serviceType = req.getParameter("serviceType");        String time = req.getParameter("time");        String checked = req.getParameter("checkBox");        String location = req.getParameter("location");        if(checked != null){            try {                location = a.getHomeAddress(userId);            } catch (SQLException throwables) {                throwables.printStackTrace();            }        }        String message = req.getParameter("message");        String sql = "insert into appointment " +                "(serviceType,time,location,message,userId)" +                " values ('";        sql += serviceType + "','";        sql += time + "','";        sql += location + "','";        sql += message + "',";        sql += userId + ")";        a.execute(sql);        req.setAttribute("userId",userId);        req.getRequestDispatcher("consumer.jsp").forward(req,resp);    }}