package consumer;import database.databaseAccess;import jakarta.servlet.ServletException;import jakarta.servlet.http.HttpServlet;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import java.io.IOException;import java.util.ArrayList;public class update extends HttpServlet {    @Override    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        String userId = req.getParameter("userId");        ArrayList<String> parameterList = new ArrayList<String>();        parameterList.add("nameOfClient:" + req.getParameter("nameOfClient"));        parameterList.add("homeAddress:" + req.getParameter("homeAddress"));        parameterList.add("contactPhoneNumber:" + req.getParameter("contactPhoneNumber"));        parameterList.add("emailAddress:" + req.getParameter("emailAddress"));        parameterList.add("initialPassword:" + req.getParameter("initialPassword"));        parameterList.add("extraInformation:" + req.getParameter("extraInformation"));        parameterList.add("nameOnInvoice:" + req.getParameter("nameOnInvoice"));        parameterList.add("billerEmailAddress:" + req.getParameter("billerEmailAddress"));        makeSql(parameterList,userId);        req.setAttribute("userId",userId);        req.getRequestDispatcher("consumer.jsp").forward(req,resp);    }    public void makeSql(ArrayList<String> parameterList, String userId){        databaseAccess b = new databaseAccess();        for(int i = 0; i < parameterList.size(); i++){            String[] a = parameterList.get(i).split(":");            if(a.length == 2){                String sql = "update users set " + a[0] + "='"+ a[1] +"' where userId=" + userId;                System.out.println(sql);                b.execute(sql);            }        }    }}