import database.databaseAccess;import jakarta.servlet.ServletException;import jakarta.servlet.http.HttpServlet;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import java.io.IOException;public class register extends HttpServlet {    @Override    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        String nameOfClient = req.getParameter("nameOfClient");        String homeAddress = req.getParameter("homeAddress");        String contactPhoneNumber = req.getParameter("contactPhoneNumber");        String emailAddress = req.getParameter("emailAddress");        String initialPassword = req.getParameter("initialPassword");        String extraInformation = req.getParameter("extraInformation");        String nameOnInvoice = req.getParameter("nameOnInvoice");        String billerEmailAddress = req.getParameter("billerEmailAddress");        String sql = "insert into users " +                "(nameOfClient,homeAddress,contactPhoneNumber,emailAddress,initialPassword,extraInformation,nameOnInvoice,billerEmailAddress)" +                " values ('";        sql += nameOfClient + "','";        sql += homeAddress + "','";        sql += contactPhoneNumber + "','";        sql += emailAddress + "','";        sql += initialPassword + "','";        sql += extraInformation + "','";        sql += nameOnInvoice + "','";        sql += billerEmailAddress + "')";        System.out.println(sql);        new databaseAccess().execute(sql);        resp.sendRedirect("login.jsp");    }}